mapper.update:
UPDATE dept
      SET dname = #{dname}
           ,loc = #{loc}
  WHERE deptno = #{deptno}

mapper.select:
SELECT
       t_dept.deptno, t_dept.dname, t_dept.loc
  FROM ( 
        SELECT 
               rownum rnum, deptno, dname, loc
          FROM dept  
       )t_dept


		 AND t_dept.rnum >= #{start} 


		 AND t_dept.rnum <=#{end} 


		AND t_dept.deptno = #{deptno}


		AND t_dept.dname LIKE '%'||#{dname}||'%'


		AND t_dept.loc LIKE '%'||#{loc}||'%'


ORDER BY t_dept.deptno asc

SELECT
       t_dept.deptno, t_dept.dname, t_dept.loc
  FROM ( 
        SELECT 
               rownum rnum, deptno, dname, loc
          FROM dept  
       )t_dept


		 AND t_dept.rnum >= #{start} 


		 AND t_dept.rnum <=#{end} 


		AND t_dept.deptno = #{deptno}


		AND t_dept.dname LIKE '%'||#{dname}||'%'


		AND t_dept.loc LIKE '%'||#{loc}||'%'


ORDER BY t_dept.deptno asc
